import React, { useState, useCallback, useEffect } from "react";
import { Alert } from "react-native";
import { useSelector } from "react-redux";

import { fullList } from "../../reduxApi/notesList/notesSelectors";
import NoteList from "./NoteList";
import useDeleteNote from "../../helpers/useDeleteNote";
import useUpdateStatus from "../../helpers/useUpdateNote";
import {
  SET_FULL_LIST,
  UPDATE_FULL_LIST,
} from "../../reduxApi/notesList/notesToolkit";

function NoteListContainer() {
  const fullNotesList = useSelector(fullList);

  const [showModal, setShowModal] = useState(false);
  const [currentState, setCurrentState] = useState("");
  const [buttonStyle, setButtonStyle] = useState("");

  const deleteNote = useDeleteNote();
  const updateNoteStatus = useUpdateStatus();

  const updateStatus = (arr, id, requestAction, status) => {
    updateNoteStatus(arr, id, requestAction, status);
  };

  // ---------------------------------------//

  const deleteOneTodo = useCallback(
    (id, showMes, mesState) =>
      Alert.alert("Delete this todo?", "", [
        {
          text: "No",
          onPress: () => console.log(`Cancel Pressed on RenderTodo ${id}`),
          style: "cancel",
        },
        {
          text: "Yes",
          onPress: () => {
            deleteNote(fullNotesList, id, UPDATE_FULL_LIST);
            showMes(!mesState);
            setTimeout(() => showMes(mesState), 600);
          },
        },
      ]),
    [fullNotesList]
  );

  const showAllNotes = (arr, e) => {
    setCurrentState([...arr]);
    setButtonStyle(e);
  };

  const showDoneNotes = (arr, e) => {
    let markedArray = arr.filter((element) => !element.todoStatus);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  const showDoingNotes = (arr, e) => {
    let markedArray = arr.filter((element) => element.todoStatus == true);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  return (
    <NoteList>
      {fullNotesList}
      {buttonStyle}
      {showAllNotes}
      {showDoneNotes}
      {showDoingNotes}
      {showModal}
      {updateStatus}
      {deleteOneTodo}
      {setShowModal}
    </NoteList>
  );
}

export default NoteListContainer;
