import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { SET_FULL_LIST } from "../reduxApi/notesList/notesToolkit";

function useAddNotes(currentTitile) {
  const date = new Date().toLocaleDateString();
  const time = new Date().toLocaleTimeString();
  const newTodo = {
    id: Date.now().toString(),
    title: currentTitile,
    todoStatus: true,
    date: `${date} ${time}`,
  };

  return () => {
    const dispatch = useDispatch();
    return dispatch(SET_FULL_LIST(newTodo));
  };
}

export default useAddNotes;

export function useFetchEvents() {
  const dispatch = useDispatch();
  const { items, loading } = useSelector((state) => state.events);
  return () => {
    if (items.length === 0) {
      // Redux action. requestEvents returns object with type.
      dispatch(requestEvents(true));
    }
  };
}
