import React, { useState, useMemo, useCallback, useEffect } from "react";
import {
  FlatList,
  StyleSheet,
  Button,
  Text,
  View,
  Alert,
  Modal,
} from "react-native";
import ListRenderedTodos from "./ListRenderedTodos";

function RenderTodo(props) {
  const {
    children: [mainTodo, letDeleteTodo, updateStatus],
  } = props;

  // console.log(mainTodo);
  // console.log(filtredTodo);

  // свайп влево выполнит:
  // 1. Показ кнопки "Return to Doing"
  // 2. По клику вызов фунции, которая
  //  2.1 Поменяет на текущем элементе статус на todoStatus

  const [showModal, setShowModal] = useState(false);
  const [currentState, setCurrentState] = useState("");
  const [buttonStyle, setButtonStyle] = useState("");

  const ListRenderedTodosMemo = useMemo(() => ListRenderedTodos, [mainTodo]);

  const DeleteOneTodo = useCallback(
    (letDeleteTodo, id, showMes, mesState) =>
      Alert.alert("Delete this todo?", "", [
        {
          text: "No",
          onPress: () => console.log(`Cancel Pressed on RenderTodo ${id}`),
          style: "cancel",
        },
        {
          text: "Yes",
          onPress: () => {
            letDeleteTodo(id);
            showMes(!mesState);
            setTimeout(() => showMes(mesState), 600);
          },
        },
      ]),
    [mainTodo]
  );

  useEffect(() => {
    setCurrentState([...mainTodo]);
  }, [mainTodo]);

  const showAllNotes = (arr, e) => {
    setCurrentState([...arr]);
    setButtonStyle(e);
  };

  const showDoneNotes = (arr, e) => {
    let markedArray = arr.filter((element) => !element.todoStatus);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  const showDoingNotes = (arr, e) => {
    let markedArray = arr.filter((element) => element.todoStatus == true);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  //У меня должно быть 2 стейта - один это неизменный массив данных mainTodo
  // Второй - это отфильтрованый массив данных - filtred

  console.log("el", currentState);
  return (
    <View>
      <View style={styles.buttonContainer}>
        <View
          style={buttonStyle === "Full" ? styles.getButton : styles.basicButton}
        >
          <Button
            title="Full List"
            onPress={() => showAllNotes(mainTodo, "Full")}
          />
        </View>
        <View
          style={buttonStyle === "Done" ? styles.getButton : styles.basicButton}
        >
          <Button
            title="Done List"
            onPress={() => showDoneNotes(mainTodo, "Done")}
          />
        </View>
        <View
          style={
            buttonStyle === "Doing" ? styles.getButton : styles.basicButton
          }
        >
          <Button
            title="Doing List"
            onPress={() => showDoingNotes(mainTodo, "Doing")}
          />
        </View>
      </View>

      <Modal animationType={"fade"} transparent={true} visible={showModal}>
        <View style={styles.modal}>
          <Text style={styles.text}>Todo Deleted!</Text>
        </View>
      </Modal>

      <FlatList
        contentContainerStyle={listStyle.containerStyle}
        data={currentState}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item, index }) => (
          <ListRenderedTodosMemo
            DeleteOneTodo={DeleteOneTodo}
            letDeleteTodo={letDeleteTodo}
            item={item}
            setShowModal={setShowModal}
            showModal={showModal}
            index={index}
            updateStatus={updateStatus}
          />
        )}
      />
    </View>
  );
}

const listStyle = {
  containerStyle: {
    paddingBottom: "65%",
  },
};

const styles = StyleSheet.create({
  modal: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.7)",
    paddingVertical: "50%",
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },
  basicButton: {
    margin: 5,
  },
  getButton: {
    margin: 5,
    borderBottomColor: "white",
    borderBottomWidth: 2,
  },
  text: {
    fontSize: 20,
    alignItems: "center",
    color: "rgba(180, 60, 60, 0.9)",
    marginTop: 10,
  },
});

export default RenderTodo;
