import React, { useState, useMemo, useCallback, useEffect } from "react";
import { FlatList, StyleSheet, View, Alert } from "react-native";
import NoteList from "./NoteList";

function NoteListContainer(props) {
  const {
    children: [mainTodo, letDeleteTodo, updateStatus],
  } = props;

  // console.log(mainTodo);
  // console.log(filtredTodo);

  // свайп влево выполнит:
  // 1. Показ кнопки "Return to Doing"
  // 2. По клику вызов фунции, которая
  //  2.1 Поменяет на текущем элементе статус на todoStatus

  const [showModal, setShowModal] = useState(false);
  const [currentState, setCurrentState] = useState("");
  const [buttonStyle, setButtonStyle] = useState("");

  const deleteOneTodo = useCallback(
    (letDeleteTodo, id, showMes, mesState) =>
      Alert.alert("Delete this todo?", "", [
        {
          text: "No",
          onPress: () => console.log(`Cancel Pressed on RenderTodo ${id}`),
          style: "cancel",
        },
        {
          text: "Yes",
          onPress: () => {
            letDeleteTodo(id);
            showMes(!mesState);
            setTimeout(() => showMes(mesState), 600);
          },
        },
      ]),
    [mainTodo]
  );

  useEffect(() => {
    setCurrentState([...mainTodo]);
  }, [mainTodo]);

  const showAllNotes = (arr, e) => {
    setCurrentState([...arr]);
    setButtonStyle(e);
  };

  const showDoneNotes = (arr, e) => {
    let markedArray = arr.filter((element) => !element.todoStatus);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  const showDoingNotes = (arr, e) => {
    let markedArray = arr.filter((element) => element.todoStatus == true);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  //У меня должно быть 2 стейта - один это неизменный массив данных mainTodo
  // Второй - это отфильтрованый массив данных - filtred

  console.log("el", currentState);
  return (
    <NoteList>
      {mainTodo}
      {currentState}
      {buttonStyle}
      {showModal}
      {letDeleteTodo}
      {updateStatus}
      {showAllNotes}
      {showDoneNotes}
      {showDoingNotes}
      {deleteOneTodo}
      {setShowModal}
    </NoteList>
  );
}

export default NoteListContainer;
