import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, View } from "react-native";
import Navbar from "./src/Navbar/Navbar";
import AddTodo from "./src/AddTodo";
import RenderTodo from "./src/RenderTodo";
import { Provider } from "react-redux";
import store from "./store";

export default function App() {
  const [mainTodo, setMainTodo] = useState([]);

  const letAddTodo = (currentTitile) => {
    const date = new Date().toLocaleDateString();
    const time = new Date().toLocaleTimeString();
    const newTodo = {
      id: Date.now().toString(),
      title: currentTitile,
      todoStatus: true,
      date: `${date} ${time}`,
    };
    setMainTodo((prev) => [...prev, newTodo]);
  };

  const letDeleteTodo = (id) => {
    setMainTodo((prev) => prev.filter((item) => item.id !== id));
  }; /* We have to delete my todo if item.id == id, or elem have to save if item.id !== id. Remember, filter return TRUE data */

  /* Next time i going to do cheker for todoStatus in mainTodo-state, but i dont know how identificate elem-id from current element (like e.target.data-path in react)*/
  const updateStatus = (id, status) => {
    const itemData = mainTodo.map((item) =>
      item.id === id ? { ...item, todoStatus: status } : item
    );
    setMainTodo([...itemData]);
  };

  return (
    <Provider store={store}>
      <View style={styles.mainContainer}>
        <Navbar title={"Yours Todo"} />

        <View style={styles.wrapperContainer}>
          <AddTodo onSubmit={letAddTodo} />
        </View>

        <View style={styles.renderBlock}>
          <RenderTodo>
            {mainTodo}
            {letDeleteTodo}
            {updateStatus}
          </RenderTodo>
        </View>
      </View>
    </Provider>
  );
}

const styles = StyleSheet.create({
  mainContainer: {
    flex: 1,
    flexDirection: "column",
    backgroundColor: "#111",
    justifyContent: "flex-start",
  },
  wrapperContainer: {
    paddingHorizontal: 15,
    paddingVertical: 10,
  },
  renderBlock: {
    marginTop: 10,
    justifyContent: "space-between",
    paddingHorizontal: 15,
    width: "100%",
  },
});
