import { useDispatch } from "react-redux";

export default function useDeleteTodo(id) {
  const dispatch = useDispatch();
  const clearedList = arr.filter((item) => item.id !== id);
  return (requestAction) => {
    dispatch(requestAction(clearedList));
  };
}
