import React, { useState, useCallback, useEffect } from "react";
import { Alert } from "react-native";
import { useSelector } from "react-redux";

import { fullList } from "../../reduxApi/notesList/notesSelectors";
import NoteList from "./NoteList";
import useDeleteTodo from "../../helpers/useDeleteNote";
import { SET_FULL_LIST } from "../../reduxApi/notesList/notesToolkit";

function NoteListContainer() {
  const fullNotesList = useSelector(fullList);
  const [showModal, setShowModal] = useState(false);
  const [currentState, setCurrentState] = useState("");
  const [buttonStyle, setButtonStyle] = useState("");
  const [mainTodo, setMainTodo] = useState();
  const deleteTodo = useDeleteTodo();

  console.log("deleteTodo", deleteTodo);

  const letDeleteTodo = (id) => {
    setMainTodo((prev) => prev.filter((item) => item.id !== id));
  }; /* We have to delete my todo if item.id == id, or elem have to save if item.id !== id. Remember, filter return TRUE data */

  /* Next time i going to do cheker for todoStatus in mainTodo-state, but i dont know how identificate elem-id from current element (like e.target.data-path in react)*/
  const updateStatus = (id, status) => {
    const itemData = mainTodo.map((item) =>
      item.id === id ? { ...item, todoStatus: status } : item
    );
    setMainTodo([...itemData]);
  };

  // ---------------------------------------//

  const deleteOneTodo = useCallback(
    (letDeleteTodo, id, showMes, mesState) =>
      Alert.alert("Delete this todo?", "", [
        {
          text: "No",
          onPress: () => console.log(`Cancel Pressed on RenderTodo ${id}`),
          style: "cancel",
        },
        {
          text: "Yes",
          onPress: () => {
            letDeleteTodo(id);
            showMes(!mesState);
            setTimeout(() => showMes(mesState), 600);
          },
        },
      ]),
    [mainTodo]
  );

  useEffect(() => {
    setCurrentState(fullNotesList); // Тут я закончил на адекватном рендере данных после ввода
    setMainTodo(fullNotesList);
  }, [fullNotesList]);

  const showAllNotes = (arr, e) => {
    setCurrentState([...arr]);
    setButtonStyle(e);
  };

  const showDoneNotes = (arr, e) => {
    let markedArray = arr.filter((element) => !element.todoStatus);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  const showDoingNotes = (arr, e) => {
    let markedArray = arr.filter((element) => element.todoStatus == true);
    setCurrentState([...markedArray]);
    setButtonStyle(e);
  };

  console.log("mainTodo", mainTodo);

  return (
    <NoteList>
      {mainTodo}
      {buttonStyle}
      {showAllNotes}
      {showDoneNotes}
      {showDoingNotes}
      {currentState}
      {showModal}
      {letDeleteTodo}
      {updateStatus}
      {deleteOneTodo}
      {setShowModal}
    </NoteList>
  );
}

export default NoteListContainer;
