import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { StyleSheet, View } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { SET_FULL_LIST } from "./src/reduxApi/notesList/notesToolkit";

import Navbar from "./src/components/Navbar/Navbar";
import AddTodo from "./src/components/InputNoteField/InputNoteField";
import NoteListContainer from "./src/pages/NotesList/NoteListContainer";
import { Provider } from "react-redux";
import store from "./store";

export default function App() {
  const dispatch = useDispatch();
  const addNotes = (currentTitile) => {
    const date = new Date().toLocaleDateString();
    const time = new Date().toLocaleTimeString();
    const newTodo = {
      id: Date.now().toString(),
      title: currentTitile,
      todoStatus: true,
      date: `${date} ${time}`,
    };
    dispatch(SET_FULL_LIST(newTodo));
  };

  return (
    <Provider store={store}>
      <View style={styles.mainContainer}>
        <Navbar title={"Yours Todo"} />

        <AddTodo addNotes={addNotes} />

        <NoteListContainer />
      </View>
    </Provider>
  );
}

const styles = StyleSheet.create({
  mainContainer: {
    flex: 1,
    flexDirection: "column",
    backgroundColor: "#111",
    justifyContent: "flex-start",
  },
});
