import React from "react";
import { StyleSheet, Button, View } from "react-native";

export default function FolderButtons(props) {
  const {
    children: [showAllNotes, showDoneNotes, showDoingNotes, mainTodo],
  } = props;
  const [buttonStyle, setButtonStyle] = useState("");
  return (
    <View style={styles.buttonContainer}>
      <View
        style={buttonStyle === "Full" ? styles.getButton : styles.basicButton}
      >
        <Button
          title="Full List"
          onPress={() => showAllNotes(mainTodo, "Full")}
        />
      </View>
      <View
        style={buttonStyle === "Done" ? styles.getButton : styles.basicButton}
      >
        <Button
          title="Done List"
          onPress={() => showDoneNotes(mainTodo, "Done")}
        />
      </View>
      <View
        style={buttonStyle === "Doing" ? styles.getButton : styles.basicButton}
      >
        <Button
          title="Doing List"
          onPress={() => showDoingNotes(mainTodo, "Doing")}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },
  basicButton: {
    margin: 5,
  },
  getButton: {
    margin: 5,
    borderBottomColor: "white",
    borderBottomWidth: 2,
  },
});
