import React, { useState, useMemo, useCallback, useEffect } from "react";
import { FlatList, StyleSheet, View, Alert } from "react-native";
import ListRenderedTodos from "./ListRenderedTodos";
import FolderButtons from "../FolderButtons/FolderButtons";
import ModalDeletedNote from "../ModalWindows/ModalDeletedNote";

function NoteList({ children }) {
  const [
    mainTodo,
    currentState,
    buttonStyle,
    showModal,
    letDeleteTodo,
    updateStatus,
    showAllNotes,
    showDoneNotes,
    showDoingNotes,
    deleteOneTodo,
    setShowModal,
  ] = children;

  const ListRenderedTodosMemo = useMemo(() => ListRenderedTodos, [mainTodo]);

  return (
    <View style={styles.renderBlock}>
      <FolderButtons>{children}</FolderButtons>
      <ModalDeletedNote showModal={showModal} />
      <FlatList
        contentContainerStyle={listStyle.containerStyle}
        data={currentState}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item, index }) => (
          <ListRenderedTodosMemo
            deleteOneTodo={deleteOneTodo}
            letDeleteTodo={letDeleteTodo}
            item={item}
            setShowModal={setShowModal}
            showModal={showModal}
            index={index}
            updateStatus={updateStatus}
          />
        )}
      />
    </View>
  );
}

const listStyle = {
  containerStyle: {
    paddingBottom: "65%",
  },
};

const styles = StyleSheet.create({
  renderBlock: {
    marginTop: 10,
    justifyContent: "space-between",
    paddingHorizontal: 15,
    width: "100%",
  },
});

export default NoteList;
